import React from 'react';
import './Profile.css'; // Ensure you have a CSS file for styling

const Profile = ({ userProfile }) => {
    if (!userProfile) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img src={userProfile.profileImage} alt="Profile" className="profile-image" />
                <h2 className="profile-username">{userProfile.displayName}</h2>
                <p className="profile-email">{userProfile.email}</p>
            </div>
            <div className="profile-info">
                <h3>About</h3>
                <p>Welcome to your Harmonize profile! Enjoy your music journey.</p>
                {/* Add more personal info or additional sections here */}
            </div>
        </div>
    );
};

export default Profile;
