import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();

const getTokenFromUrl = () => {
  return window.location.hash.substring(1).split('&').reduce((initial, item) => {
    let parts = item.split('=');
    initial[parts[0]] = decodeURIComponent(parts[1]);
    return initial
  }, {});
}

const getRefreshTokenFromUrl = () => {
  return window.location.hash.substring(1).split('&').reduce((initial, item) => {
    let parts = item.split('=');
    initial[parts[1]] = decodeURIComponent(parts[1]);
    return initial
  }, {});
}


function App() {
  const [spotifyToken, setSpotifyToken] = useState("");
  const [nowPlaying, setNowPlaying] = useState({});
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    console.log("This is what we got from the URL: ", getTokenFromUrl());
    const spotifyToken = getTokenFromUrl().access_token;
    const refreshToken = getRefreshTokenFromUrl().refresh_token;
    window.location.hash = "";
    console.log("This is our spotify token: ", spotifyToken);
    console.log("This is our refresh token: ", refreshToken);

    if (spotifyToken) {
      setSpotifyToken(spotifyToken);
      spotifyApi.setAccessToken(spotifyToken);
      spotifyApi.getMe().then((user) => {
        console.log(user);
      });
      setLoggedIn(true);
    }
  })

  // useEffect(() => {
  //   if(!refreshToken || !expiresIn) return;
  //   const timeout = setTimeout(() => {
  //     // Refresh the token
  //   }, (expiresIn - 60) * 1000);
  // }, [refreshToken, expiresIn])



  const getNowPlaying = () => {
    spotifyApi.getMyCurrentPlaybackState()
      .then((response) => {
        console.log("Got response: ", response);
        setNowPlaying({
          name: response.item.name,
          albumArt: response.item.album.images[0].url})
      }).catch((err) => {
        console.log("Error getting current playback state: ", err);
        setNowPlaying({
          name: "No song playing",
          albumArt: ""
        })
      })
  }

  return (
    <div className="App">
      {!loggedIn && <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
        <a className="btn btn-success btn-lg" href="http://localhost:8888/login">Login to spotify</a>
        </Container>}
      {loggedIn && (
        <>
          <div>Now Playing: {nowPlaying.name}</div>
          <div>
            <img src={nowPlaying.albumArt} style={{ height: 150 }} />
          </div>
        </>
      )}
      {loggedIn && (
        <button onClick={() => getNowPlaying()}>
          Check Now Playing
        </button>
      )}
    </div>
  );
}

export default App;
