import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { MessageContext } from './MessageContext';
import axios from 'axios';

function FriendsPage({ userProfile }) {
    const { conversations, setConversations } = useContext(MessageContext);
    const navigate = useNavigate();

    useEffect(() => {
        // Ensure userProfile and userProfile.userId exist before making the API call
        if (userProfile && userProfile.userId) {
            axios.get(`http://localhost:8888/api/conversations/user/${userProfile.userId}`)
            .then(response => {
                setConversations(response.data); // Assuming response.data is always an array, handle non-array responses accordingly
            })
            .catch(error => {
                console.error('Failed to fetch conversations:', error);
                setConversations([]); // Ensure conversations is set to an empty array on error
            });
        }
    }, [setConversations, userProfile]);

    const handleSelectConversation = (conversationId) => {
        navigate(`/chat/${conversationId}`);
    };

    return (
        <div className="middle-content">
            <h1>Friends Page</h1>
            <ul>
                {conversations && conversations.length > 0 ? conversations.map((conversation) => (
                    <li key={conversation._id} onClick={() => handleSelectConversation(conversation._id)}>
                        Chat with {conversation.userDisplayName} - Last message at {new Date(conversation.lastMessageAt).toLocaleTimeString()}
                    </li>
                )) : <p>No conversations yet.</p>}
            </ul>
        </div>
    );
}

export default FriendsPage;
