import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Sidebar = ({ userProfile, onLogout }) => {
    const navigate = useNavigate();

    const handleProfileClick = () => {
        if (userProfile && userProfile.displayName) {
            navigate(`/profile/${userProfile.displayName}`);
        } else {
            alert('User profile is not loaded yet.');
        }
    };

    return (
        <div className="sidebar">
            <div className="sidebar-header">
                <img src={userProfile?.profileImage || 'https://via.placeholder.com/150'} alt="Profile" className="sidebar-image" />
                <h2 className="sidebar-username">{userProfile?.displayName || 'Unknown'}</h2>
                <p className="sidebar-email">{userProfile?.email || 'No email provided'}</p>
            </div>
            <ul className="sidebar-menu">
                <li onClick={handleProfileClick}><a href="#">Profile</a></li>
                <li><Link to="/">Feed</Link></li>
                <li><Link to="/friends">Friends</Link></li>
            </ul>
            <div className="sidebar-footer">
                <a href="#" onClick={onLogout}>Logout</a>
            </div>
        </div>
    );
};

export default Sidebar;
