import { useState, useEffect } from "react";
import React from 'react';
import SpotifyPlayer from 'react-spotify-web-playback';

export default function Player({ spotifyToken, trackUri }) {
    const [play, setPlay] = React.useState(false);

    useEffect(() => {
        setPlay(true);
    }, [trackUri]);

    if (!spotifyToken) return null;

    const playerStyles = {
        activeColor: '#1db954', // Color of the currently playing track
        bgColor: '#333', // Background color of the player
        color: '#fff', // Text color and icon color in the player
        loaderColor: '#fff', // Color of the loading indicator
        sliderColor: '#1db954', // Color of the slider
        trackArtistColor: '#ccc', // Color of the track artist text
        trackNameColor: '#fff', // Color of the track name text
    };

    return (
        <SpotifyPlayer
            token={spotifyToken}
            showSaveIcon
            callback={state => {
                if (!state.isPlaying) setPlay(false);
            }}
            play={play}
            uris={trackUri ? [trackUri] : []}
            styles={playerStyles}
        />
    );
}
