import React, { createContext, useState, useEffect } from 'react';
import io from 'socket.io-client';

export const MessageContext = createContext();

export const MessageProvider = ({ children }) => {
    const [conversations, setConversations] = useState([]);
    const [currentConversation, setCurrentConversation] = useState(null);
    const socket = io('http://localhost:8888');
    
    const startConversation = async (otherUserId) => {
        try {
            const response = await fetch('http://localhost:8888/api/conversations/start', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ otherUserId })
            });
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            return await response.json();
        } catch (error) {
            console.error('Failed to start conversation:', error);
            throw error; // Rethrow the error so it can be caught by caller
        }
    };

    useEffect(() => {
        socket.on('receiveMessage', ({ senderId, message }) => {
            // Add received message to the appropriate conversation
            setConversations(prevConversations => {
                const newConversations = prevConversations.map(conversation => {
                    if (conversation.userId === senderId) {
                        return { ...conversation, messages: [...conversation.messages, message] };
                    }
                    return conversation;
                });
                return newConversations;
            });
        });

        return () => {
            socket.off('receiveMessage');
        };
    }, []);

    const sendMessage = ({ message, to }) => {
        socket.emit('sendMessage', { message, to });
        // Update the current conversation locally
        setConversations(prevConversations => {
            const newConversations = prevConversations.map(conversation => {
                if (conversation.userId === to) {
                    return { ...conversation, messages: [...conversation.messages, message] };
                }
                return conversation;
            });
            return newConversations;
        });
    };

    const selectConversation = (userId) => {
        const conversation = conversations.find(c => c.userId === userId);
        console.log("Selecting conversation for user:", userId, conversation);
        setCurrentConversation(conversation);
    };
    
    useEffect(() => {
        console.log("Conversations updated:", conversations);
    }, [conversations]);
    

    return (
        <MessageContext.Provider value={{ conversations, startConversation, selectConversation, sendMessage, currentConversation }}>
            {children}
        </MessageContext.Provider>
    );
};
