import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import axios from 'axios';

function FeedPage({ userProfile }) {
    const [content, setContent] = useState('');
    const [tags, setTags] = useState({ artist: '', song: '', album: '' });
    const [posts, setPosts] = useState([]); // State to store fetched posts
  
    // Fetch posts from backend when component mounts
    useEffect(() => {
      const fetchPosts = async () => {
        try {
          const response = await axios.get('http://localhost:8888/posts');
          setPosts(response.data); // Set fetched posts to state
        } catch (error) {
          console.error('Error fetching posts:', error);
        }
      };
      fetchPosts();
    }, []); // Empty dependency array means this effect runs once on mount
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      if (!userProfile) {
        alert("You need to be logged in to post.");
        return;
      }
      try {
        const post = {
          content,
          tags,
          user: userProfile.spotifyId // Pass Spotify ID as the user reference
        };
        await axios.post('http://localhost:8888/posts', post);
        setContent('');
        setTags({ artist: '', song: '', album: '' });
        // Fetch posts again to update the list after posting
        await fetchPosts();
      } catch (error) {
        console.error('Error creating post:', error);
        alert('Failed to create post');
      }
    };
  
    // Function to render each post
    const renderPosts = () => {
      return posts.map((post) => (
        <Card key={post._id} className="mb-3">
          <Card.Body>
            <Card.Title>{post.user.displayName}</Card.Title>
            <Card.Text>{post.content}</Card.Text>
            <Card.Subtitle className="mb-2 text-muted">Tags: {post.tags.artist}, {post.tags.song}, {post.tags.album}</Card.Subtitle>
            {/* Add buttons for likes and replies here if needed */}
          </Card.Body>
        </Card>
      ));
    };
  
  
  return (
    <Container className="middle-content">
      <h1>Feed Page</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="postContent">
          <Form.Label>What's on your mind?</Form.Label>
          <Form.Control
            type="text"
            placeholder="Write something..."
            value={content}
            onChange={e => setContent(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="postTags">
          <Form.Label>Tags</Form.Label>
          <Form.Control
            type="text"
            placeholder="Artist"
            value={tags.artist}
            onChange={e => setTags({ ...tags, artist: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Song"
            value={tags.song}
            onChange={e => setTags({ ...tags, song: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Album"
            value={tags.album}
            onChange={e => setTags({ ...tags, album: e.target.value })}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Post
        </Button>
      </Form>
      {renderPosts()}
    </Container>
  );
}

export default FeedPage;
