import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Card, Button } from 'react-bootstrap';

function ProfilePage() {
    const { displayName } = useParams();
    const [user, setUser] = useState(null);
    const [posts, setPosts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        axios.get(`http://localhost:8888/users/search/${displayName}`)
            .then(response => {
                if (response.data.length > 0) {
                    setUser(response.data[0]);
                    fetchUserPosts(response.data[0]._id);  // Fetch posts for this specific user
                } else {
                    setError('User not found');
                }
            })
            .catch(err => {
                setError('Failed to fetch user data');
                console.error(err);
            });
    }, [displayName]);

    const fetchUserPosts = (displayName) => {
        axios.get(`http://localhost:8888/posts`, { params: { displayName: displayName } })
            .then(response => {
                setPosts(response.data);
            })
            .catch(err => {
                console.error('Error fetching posts:', err);
                setError('Failed to fetch posts');
            });
    };

    useEffect(() => {
      if (user) {
          fetchUserPosts(displayName); // Fetch posts after user is fetched and based on displayName
      }
    }, [user, displayName]); // Depend on user and displayName

    const renderPosts = () => {
        return posts.map(post => (
            <Card key={post._id} className="mb-3">
                <Card.Body>
                    <div className="post-header">
                        <div className="user-info">
                            <img src={user.profileImage || 'https://via.placeholder.com/150'} alt="Profile" className="post-profile-image" />
                            <div>{user.displayName}</div>
                        </div>
                        <div className="tag-section">
                            {post.tags.artist && <div className="tag">Artist: {post.tags.artist}</div>}
                            {post.tags.song && <div className="tag">Song: {post.tags.song}</div>}
                            {post.tags.album && <div className="tag">Album: {post.tags.album}</div>}
                        </div>
                    </div>
                    <Card.Text>{post.content}</Card.Text>
                </Card.Body>
            </Card>
        ));
    };

    if (error) {
        return <Container className="middle-content"><p>{error}</p></Container>;
    }

    return (
        <Container className="middle-content">
            {user ? (
                <>
                    <h1>{user.displayName}</h1>
                    <img src={user.profileImage || 'https://via.placeholder.com/150'} alt={`${user.displayName}`} style={{ borderRadius: '50%' }} />
                    <Button onClick={() => {/* Handle messaging logic here */}}>Message</Button>
                    {renderPosts()}
                </>
            ) : (
                <p>Loading user profile...</p>
            )}
        </Container>
    );
}

export default ProfilePage;
