import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import axios from 'axios';

function FeedPage({ userProfile }) {
  console.log(userProfile);
  const [content, setContent] = useState('');
  const [tags, setTags] = useState({ artist: '', song: '', album: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userProfile) {
      alert("You need to be logged in to post.");
      return;
    }
    try {
      const post = {
        content,
        tags,
        user: userProfile.spotifyId // Pass Spotify ID as the user reference
      };
      // Assuming your backend is set up to handle posts at this endpoint
      await axios.post('http://localhost:8888/posts', post);
      // Clear the form upon success
      setContent('');
      setTags({ artist: '', song: '', album: '' });
    } catch (error) {
      console.error('Error creating post:', error);
      alert('Failed to create post');
    }
  };
  
  return (
    <Container className="middle-content">
      <h1>Feed Page</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="postContent">
          <Form.Label>What's on your mind?</Form.Label>
          <Form.Control
            type="text"
            placeholder="Write something..."
            value={content}
            onChange={e => setContent(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="postTags">
          <Form.Label>Tags</Form.Label>
          <Form.Control
            type="text"
            placeholder="Artist"
            value={tags.artist}
            onChange={e => setTags({ ...tags, artist: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Song"
            value={tags.song}
            onChange={e => setTags({ ...tags, song: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Album"
            value={tags.album}
            onChange={e => setTags({ ...tags, album: e.target.value })}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Post
        </Button>
      </Form>
    </Container>
  );
}

export default FeedPage;
