import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Card, Button } from 'react-bootstrap';

function ProfilePage() {
    const { displayName } = useParams();
    const [user, setUser] = useState(null);
    const [posts, setPosts] = useState([]);
    const [error, setError] = useState('');

    // Fetch user details
    useEffect(() => {
        axios.get(`http://localhost:8888/users/search/${displayName}`)
            .then(response => {
                if (response.data.length > 0) {
                    setUser(response.data[0]); // Take the first user found
                    fetchPosts(response.data[0]._id); // Fetch posts after user is found
                } else {
                    setError('User not found');
                }
            })
            .catch(err => {
                setError('Failed to fetch user data');
                console.error(err);
            });
    }, [displayName]);

    // Fetch posts by the user
    const fetchPosts = (userId) => {
        axios.get(`http://localhost:8888/posts`, { params: { user: userId } })
            .then(response => {
                setPosts(response.data);
            })
            .catch(err => {
                console.error('Error fetching posts:', err);
                setError('Failed to fetch posts');
            });
    };

    const renderPosts = () => {
        return posts.map((post) => (
            <Card key={post._id} className="mb-3">
                <Card.Body>
                    <Card.Title>{post.user.displayName}</Card.Title>
                    <Card.Text>{post.content}</Card.Text>
                    {/* Additional post details can go here */}
                </Card.Body>
            </Card>
        ));
    };

    if (error) {
        return <Container className="middle-content"><p>{error}</p></Container>;
    }

    return (
        <Container className="middle-content">
            {user ? (
                <>
                    <h1>{user.displayName}</h1>
                    <img src={user.profileImage || 'https://via.placeholder.com/150'} alt={`${user.displayName}`} style={{ borderRadius: '50%' }} />
                    <Button onClick={() => {/* Handle messaging logic here */}}>Message</Button>
                    <div>{renderPosts()}</div>
                </>
            ) : (
                <p>Loading user profile...</p>
            )}
        </Container>
    );
}

export default ProfilePage;
