import React, { useContext, useNavigate } from 'react';
import { MessageContext } from './MessageContext';  // Adjust the path as necessary


function FriendsPage() {
  const { conversations, selectConversation } = useContext(MessageContext); // Assuming MessageContext manages conversations
  const navigate = useNavigate();

  // Navigate to chatroom and set the current conversation
  const handleSelectConversation = (userId) => {
      selectConversation(userId);
      navigate(`/chat/${userId}`);
  };

  return (
      <div className="middle-content">
          <h1>Friends Page</h1>
          {conversations.length > 0 ? (
              <ul>
                  {conversations.map((conversation) => (
                      <li key={conversation.userId} onClick={() => handleSelectConversation(conversation.userId)}>
                          Chat with {conversation.userDisplayName}
                      </li>
                  ))}
              </ul>
          ) : (
              <p>No conversations yet.</p>
          )}
      </div>
  );
}

export default FriendsPage;
