import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { MessageContext } from './MessageContext';
import axios from 'axios';

function FriendsPage({ userProfile }) {
    const { conversations, setConversations } = useContext(MessageContext);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch the list of conversations from your API
        axios.get('http://localhost:8888/api/conversations', {
            params: { userId: userProfile.userId } // Ensure you have the userProfile context or prop available
        })
        .then(response => {
            setConversations(response.data); // Assuming you store conversations in context
        })
        .catch(error => console.error('Failed to fetch conversations:', error));
    }, [setConversations]);

    const handleSelectConversation = (conversationId) => {
        navigate(`/chat/${conversationId}`);
    };

    return (
        <div className="middle-content">
            <h1>Friends Page</h1>
            <ul>
                {conversations.length > 0 ? conversations.map((conversation) => (
                    <li key={conversation._id} onClick={() => handleSelectConversation(conversation._id)}>
                        Chat with {conversation.userDisplayName} - Last message at {new Date(conversation.lastMessageAt).toLocaleTimeString()}
                    </li>
                )) : <p>No conversations yet.</p>}
            </ul>
        </div>
    );
}

export default FriendsPage;
