import React from 'react';
import { Card, ListGroup } from 'react-bootstrap';

const Sidebar = ({ userProfile, onLogout }) => {
    // Check if userProfile is null or undefined and use default values
    const displayName = userProfile ? userProfile.displayName : 'Unknown';
    const email = userProfile ? userProfile.email : 'No email provided';
    const profileImage = userProfile && userProfile.profileImage ? userProfile.profileImage : 'https://via.placeholder.com/150'; // Default image URL

    return (
        <Card style={{ width: '18rem', backgroundColor: '#343a40', color: 'white' }}>
            <Card.Img variant="top" src={profileImage} />
            <Card.Body>
                <Card.Title>Logged in as: {displayName}</Card.Title>
                <Card.Text>{email}</Card.Text>
            </Card.Body>
            <ListGroup variant="flush">
                <ListGroup.Item action href="#profile" style={{ backgroundColor: '#343a40', color: 'white' }}>Profile</ListGroup.Item>
                <ListGroup.Item action href="#feed" style={{ backgroundColor: '#343a40', color: 'white' }}>Feed</ListGroup.Item>
                <ListGroup.Item action href="#option3" style={{ backgroundColor: '#343a40', color: 'white' }}>Option3</ListGroup.Item>
                <ListGroup.Item action href="#option4" style={{ backgroundColor: '#343a40', color: 'white' }}>Option4</ListGroup.Item>
            </ListGroup>
            <Card.Body>
                <Card.Link href="#" onClick={onLogout} style={{ color: '#1DB954' }}>Logout</Card.Link>
            </Card.Body>
        </Card>
    );
};

export default Sidebar;
