import React, { useContext, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { SearchContext } from './SearchContext'; // Assuming this is set up for global state management
import axios from 'axios';

function SearchBar() {
    const [searchTerm, setSearchTerm] = useState('');
    const { setSearchCriteria } = useContext(SearchContext);
    const location = useLocation();
    const navigate = useNavigate();

    const handleSearch = async (event) => {
        event.preventDefault();
        if (location.pathname.includes('/profile')) {
            // We're on a profile page, modify search to find users
            try {
                const response = await axios.get(`http://localhost:8888/users/search/${searchTerm}`);
                if (response.data.length > 0) {
                    navigate(`/profile/${response.data[0]._id}`); // Navigate to the first found user's profile
                } else {
                    alert('No users found');
                }
            } catch (error) {
                console.error('Error searching for users:', error);
            }
        } else {
            // We're on the feed page, search posts
            setSearchCriteria(searchTerm);
        }
    };

    return (
        <div style={{ position: 'absolute', right: '20px', top: '20px' }}>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder={location.pathname.includes('/profile') ? "Search users..." : "Search posts..."}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="submit">Search</button>
            </form>
        </div>
    );
}

export default SearchBar;
