import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Card } from 'react-bootstrap';
import axios from 'axios';

function FeedPage({ userProfile }) {
    const [content, setContent] = useState('');
    const [tags, setTags] = useState({ artist: '', song: '', album: '' });
    const [posts, setPosts] = useState([]);
  
    // Define fetchPosts outside useEffect so it can be reused
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:8888/posts');
        setPosts(response.data); // Set fetched posts to state
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
  
    // Fetch posts from backend when component mounts
    useEffect(() => {
      fetchPosts();
    }, []); // Empty dependency array means this effect runs once on mount
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      if (!userProfile) {
        alert("You need to be logged in to post.");
        return;
      }
      try {
        const post = {
          content,
          tags,
          user: userProfile.spotifyId
        };
        await axios.post('http://localhost:8888/posts', post);
        setContent('');
        setTags({ artist: '', song: '', album: '' });
        await fetchPosts(); // Now fetchPosts is defined and can be called here
      } catch (error) {
        console.error('Error creating post:', error);
        alert('Failed to create post');
      }
    };
  
    const renderPosts = () => {
      return posts.map((post) => (
        <Card key={post._id} className="mb-3">
          <Card.Body>
            <div className="post-header">
              <img src={post.user.profileImage || 'https://via.placeholder.com/150'} alt={`${post.user.displayName}'s profile`} className="post-profile-image" />
              <div>
                <Card.Title>{post.user.displayName}</Card.Title>
                <Card.Text>{post.content}</Card.Text>
              </div>
            </div>
            {/* Rest of your post rendering */}
          </Card.Body>
        </Card>
      ));
  };
  
  
    
    
    
    
    
    
  
  
  return (
    <Container className="middle-content">
      <h1>Feed Page</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="postContent">
          <Form.Label>What's on your mind?</Form.Label>
          <Form.Control
            type="text"
            placeholder="Write something..."
            value={content}
            onChange={e => setContent(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="postTags">
          <Form.Label>Tags</Form.Label>
          <Form.Control
            type="text"
            placeholder="Artist"
            value={tags.artist}
            onChange={e => setTags({ ...tags, artist: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Song"
            value={tags.song}
            onChange={e => setTags({ ...tags, song: e.target.value })}
          />
          <Form.Control
            type="text"
            placeholder="Album"
            value={tags.album}
            onChange={e => setTags({ ...tags, album: e.target.value })}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Post
        </Button>
      </Form>
      {renderPosts()}
    </Container>
  );
}

export default FeedPage;
