import React, { useContext, useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { MessageContext } from './MessageContext';
import io from 'socket.io-client';

function ChatRoom() {
    const { conversationId } = useParams();
    const { conversations, selectConversation } = useContext(MessageContext);
    const [newMessage, setNewMessage] = useState("");
    const messagesEndRef = useRef(null);
    const [socket, setSocket] = useState(null);
    
    useEffect(() => {
        // Connect to Socket.IO server
        const newSocket = io('http://localhost:8888');
        console.log(conversationId);
        setSocket(newSocket);
        return () => newSocket.close();
    }, [setSocket]);

    // Find the current conversation based on the conversationId
    const conversation = conversations.find(c => c._id === conversationId);

    // Set up socket listeners once on component mount
    useEffect(() => {
        if (socket && conversation) {
            socket.on('receiveMessage', (message) => {
                if (message.conversationId === conversationId) {
                    // Update the conversation messages here
                    selectConversation(conversationId); // Refresh or modify to update UI
                }
            });
        }
    }, [socket, conversationId, selectConversation, conversation]);

    // Auto-scroll to the latest message
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [conversation?.messages.length]);

    const handleSendMessage = () => {
        if (newMessage.trim() !== "") {
            // Send message using socket
            socket.emit('sendMessage', {
                senderId: 'yourSenderId', // Obtain from context or state
                receiverId: 'otherUserId', // Obtain from context or props
                text: newMessage,
                conversationId: conversationId
            });
            setNewMessage("");
        }
    };

    return (
        <div className="middle-content">
            <h1>Chat with {conversation?.otherUserDisplayName || "User"}</h1>
            {conversation ? (
                <>
                    <div className="messages">
                        {conversation.messages.map((msg, index) => (
                            <div key={index} className={`message ${msg.senderId === 'yourSenderId' ? 'sent' : 'received'}`}>
                                <img src={msg.senderId === 'yourSenderId' ? 'yourProfileImage' : 'otherUserProfileImage'} alt="Profile" className="profile-image"/>
                                <p>{msg.text}</p>
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                    <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type a message..."
                        className="message-input"
                    />
                    <button onClick={handleSendMessage} className="send-button">Send</button>
                </>
            ) : (
                <p>Loading conversation or start a new chat...</p>
            )}
        </div>
    );
}

export default ChatRoom;
