import React, { useContext, useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { MessageContext } from './MessageContext';
import io from 'socket.io-client';

function ChatRoom() {
    const { conversationId } = useParams();
    const { currentConversation, setCurrentConversation } = useContext(MessageContext);
    const [newMessage, setNewMessage] = useState("");
    const messagesEndRef = useRef(null);
    const [socket, setSocket] = useState(null);
    
    // Connect to Socket.IO server and clean up on unmount
    useEffect(() => {
        const newSocket = io('http://localhost:8888');
        setSocket(newSocket);
        return () => newSocket.close();
    }, []);

    // Fetch the specific conversation when the component mounts or the conversationId changes
    useEffect(() => {
        if (conversationId) {
            fetch(`http://localhost:8888/api/conversations/${conversationId}`)
                .then(response => response.json())
                .then(conversation => {
                    setCurrentConversation(conversation);
                })
                .catch(error => console.error('Failed to fetch conversation:', error));
        }
    }, [conversationId, setCurrentConversation]);

    // Listen for new messages via socket
    useEffect(() => {
        if (socket) {
            socket.on('receiveMessage', message => {
                if (message.conversationId === conversationId) {
                    // Update the conversation with the new message
                    setCurrentConversation(prev => ({
                        ...prev,
                        messages: [...prev.messages, message]
                    }));
                }
            });
        }

        return () => {
            if (socket) {
                socket.off('receiveMessage');
            }
        };
    }, [socket, conversationId, setCurrentConversation]);

    // Auto-scroll to the latest message
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [currentConversation?.messages.length]);

    const handleSendMessage = () => {
        if (newMessage.trim() !== "") {
            // Example: Assume currentUser and otherUser are objects containing user data including `_id`
            const senderId = currentUser._id; // Actual sender ID from user data
            const receiverId = otherUser._id; // Actual receiver ID from user data
    
            socket.emit('sendMessage', {
                senderId: senderId,
                receiverId: receiverId,
                text: newMessage,
                conversationId: currentConversation._id // Assuming you are within a conversation context
            });
            setNewMessage("");
        }
    };

    return (
        <div className="middle-content">
            <h1>Chat with {currentConversation?.otherUserDisplayName || "User"}</h1>
            {currentConversation ? (
                <>
                    <div className="messages">
                        {currentConversation.messages.map((msg, index) => (
                            <div key={index} className={`message ${msg.senderId === 'yourSenderId' ? 'sent' : 'received'}`}>
                                <img src={msg.senderId === 'yourSenderId' ? 'yourProfileImage' : 'otherUserProfileImage'} alt="Profile" className="profile-image"/>
                                <p>{msg.text}</p>
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                    <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type a message..."
                        className="message-input"
                    />
                    <button onClick={handleSendMessage} className="send-button">Send</button>
                </>
            ) : (
                <p>Loading conversation or start a new chat...</p>
            )}
        </div>
    );
}

export default ChatRoom;
