import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';

function FeedPage({ userProfile }) { // Pass userProfile as a prop to use it in the FeedPage
  const [content, setContent] = useState('');
  const [tags, setTags] = useState({artist: '', song: '', album: ''});

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const post = { content, tags };
      await axios.post('http://localhost:8888/posts', post, {
        headers: {
          // Assuming you're using bearer tokens
          'Authorization': `Bearer ${localStorage.getItem('token')}` // Make sure you're handling and storing the token correctly
        }
      });
      // Clear the form
      setContent('');
      setTags({artist: '', song: '', album: ''});
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="middle-content">
      <h1>Feed Page</h1>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="formBasicContent">
            <Form.Label>Content</Form.Label>
            <Form.Control 
              type="text" 
              placeholder="What's on your mind?" 
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicTags">
            <Form.Label>Tags</Form.Label>
            <Form.Control 
              type="text" 
              placeholder="Artist"
              value={tags.artist}
              onChange={(e) => setTags({ ...tags, artist: e.target.value })}
            />
            <Form.Control 
              type="text" 
              placeholder="Song"
              value={tags.song}
              onChange={(e) => setTags({ ...tags, song: e.target.value })}
            />
            <Form.Control 
              type="text" 
              placeholder="Album"
              value={tags.album}
              onChange={(e) => setTags({ ...tags, album: e.target.value })}
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Container>
    </div>
  );
}

export default FeedPage;
